document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".faq-item");e.forEach(t=>{const a=t.querySelector(".faq-question");a.addEventListener("click",()=>{const c=t.classList.contains("active");e.forEach(e=>{e.classList.remove("active"),e.querySelector(".faq-question").setAttribute("aria-expanded","false")}),c||(t.classList.add("active"),a.setAttribute("aria-expanded","true"))})})});